import { Card, CardContent, CardActions, Typography, Button, Chip, Stack, Box } from '@mui/material';
import { Visibility as VisibilityIcon, Delete as DeleteIcon } from '@mui/icons-material';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { useState } from 'react';

const BookingCard = ({ booking, onDelete, onViewDetails }) => {
  const [openDialog, setOpenDialog] = useState(false);

  const statusColors = {
    'PENDING': 'warning',
    'CONFIRMED': 'info', 
    'PAID': 'success',
    'CANCELLED': 'error',
    'COMPLETED': 'secondary'
  };

  const handleClickDelete = () => {
    setOpenDialog(true);
  };

  const handleConfirmDelete = () => {
    onDelete(booking.bookingId);
    setOpenDialog(false);
  };

  return (
    <Card sx={{ mb: 2 }}>
      <CardContent>
        <Stack spacing={2}>
          <Stack direction="row" spacing={2}>
            <Box
              component="img"
              src={booking.tour?.thumbnailUrl}
              alt={booking.tour?.name}
              sx={{
                width: 200,
                height: 150,
                objectFit: 'cover'
              }}
              onError={(e) => {
                e.target.src = 'data:image/png;base64,'
              }}
            />
            <Stack spacing={1}>
              <Typography variant="h6">{booking.tour?.name}</Typography>
              <Chip 
                label={booking.status}
                color={statusColors[booking.status]}
              />
              <Typography>
                <strong>Mã đặt tour:</strong> {booking.bookingId}
              </Typography>
              <Typography>
                <strong>Ngày tạo:</strong> {new Date(booking.createdAt).toLocaleString('vi-VN')}
              </Typography>
            </Stack>
          </Stack>

          <Stack spacing={1}>
            <Typography>
              <strong>Người liên hệ:</strong> {booking.contactFullName}
            </Typography>
            <Typography>
              <strong>Số điện thoại:</strong> {booking.contactPhoneNumber}
            </Typography>
            <Typography>
              <strong>Email:</strong> {booking.contactEmail}
            </Typography>
            <Typography>
              <strong>Số người:</strong> {booking.numberOfParticipants}
            </Typography>
            <Typography>
              <strong>Tổng tiền:</strong> {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(booking.totalPrice)}
            </Typography>
          </Stack>
        </Stack>
      </CardContent>

      <CardActions>
        <Button
          variant="contained"
          startIcon={<VisibilityIcon />}
          onClick={() => onViewDetails(booking.bookingId)}
        >
          Chi tiết
        </Button>
        <Button
          variant="contained"
          color="error"
          startIcon={<DeleteIcon />}
          onClick={handleClickDelete}
        >
          Xóa
        </Button>
      </CardActions>

      <Dialog
        open={openDialog}
        onClose={() => setOpenDialog(false)}
      >
        <DialogTitle>Xác nhận xóa</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
          <Button onClick={handleConfirmDelete} color="error" autoFocus>
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Card>
  );
};

export default BookingCard;